import React from 'react'
import {ItemWrapper, ItemVideo} from './style'
import styled from 'styled-components';

export const ItemImage = styled.div`
  background: ${props => `url(${props.background})`};
  background-position: top;
  background-repeat: no-repeat;
  background-size: cover;
  height: 300px;
  width: 100%;
  border: 3px solid white;

  @media only screen and (min-width: 450px){
   height: 500px;
  }

  @media only screen and (min-width: 768px){
   height: 300px;
  }
 `

type Props = {
 gallery: Object
}

export const ImageGalleryItem = (props: Props) => {
 console.log('GALLERY: ', props.gallery.images)
 const firstItem = props.gallery.images ? props.gallery.images[0] : null
 const content = firstItem ? 
   firstItem.type === 'video/mp4' ? (
    <>
     <ItemVideo controls>
      <source src={firstItem.mp4} type={firstItem.type} />
      Your browser does not support the video tag.
     </ItemVideo>
    </>
   ) : <ItemImage background={firstItem.link}/>
   // ) : <ImageWrapper><ItemImage src={firstItem.link}/></ImageWrapper>
  : null
 // const videoItem = props.gallery.images ? props.gallery.images.filter(item => item.type === 'image/png' 
  // ) :  null

 // console.log('ITEM: ', videoItem)
 return (
  <ItemWrapper>
  {/* <h3>{props.gallery.images_count}</h3>  */}
   {content}
   {/* <ItemDetails>
    <p>{props.gallery.title}</p>
   </ItemDetails> */}
  </ItemWrapper>
 )
}

export default ImageGalleryItem
