import React from 'react'
import {DetailsWrapper} from './style'
import styled from 'styled-components';

export const ItemImage = styled.div`
  background: ${props => `url(${props.background})`};
  background-position: top;
  background-repeat: no-repeat;
  background-size: cover;
  /* height: 300px; */
  width: 100%;
  border: 3px solid white;

  @media only screen and (min-width: 450px){
   height: 500px;
  }

  @media only screen and (min-width: 768px){
   height: 300px;
  }
 `

type Props = {
 gallery: Object
}

export const ImageGalleryItem = (props: Props) => {
 const {images, title, description, upvotes, downvotes, score} = props.location.state.params

 console.log('PROPS: ', props.location.state.params)

 const image = images ? images.map(item => {
  return item.type === 'video/mp4' ? (
   <>
      <video controls>
       <source src={item.mp4} type={item.type} />
       Your browser does not support the video tag.
      </video>
     </>
  ) : <ItemImage background={item.link}/>
 }) :null


 return (
  <DetailsWrapper>
   <h2>{image}</h2>
  </DetailsWrapper>
 )
}

export default ImageGalleryItem
